import { getProcessId } from '@/utils/process';
export const foreground = updateSession => id => updateSession({
  foregroundId: id
});
export const getState = session => ({
  id,
  name = ''
}) => session.states[id || getProcessId(name)] || {};
export const saveState = (session, updateSession) => ({
  id,
  height = 0,
  width = 0,
  x = 0,
  y = 0
}) => {
  const {
    x: previousX = 0,
    y: previousY = 0
  } = session.states[id] || {};
  updateSession({
    state: {
      id,
      height,
      width,
      x: previousX === x ? x : previousX + x,
      y: previousY === y ? y : previousY + y
    }
  });
};
